<template>
    <v-card max-width="448" class="mx-auto" color="grey-lighten-3">
        <v-layout>
            <v-app-bar color="teal-darken-4" image="https://picsum.photos/1920/1080?random">
                <template v-slot:image>
                    <v-img gradient="to top right, rgba(19,84,122,.8), rgba(128,208,199,.8)"></v-img>
                </template>

                <template v-slot:prepend>
                    <v-app-bar-nav-icon></v-app-bar-nav-icon>
                </template>

                <v-app-bar-title>Instagram</v-app-bar-title>

                <v-spacer></v-spacer>

                <v-btn icon>
                    <v-icon>mdi-magnify</v-icon>
                </v-btn>

                <v-btn icon>
                    <v-icon>mdi-heart</v-icon>
                </v-btn>

                <v-btn icon>
                    <v-icon>mdi-dots-vertical</v-icon>
                </v-btn>
            </v-app-bar>

            <v-main> </v-main>
        </v-layout>
    </v-card>
    <v-container>
        <v-row>
            <v-col v-for="post in posts" :key="post.id" cols="12" md="6" lg="4">
                <v-card to="/user-profile">
                    <v-text class="mx-5" color="primary"> {{post.caption}}</v-text>
                    <v-img :src="post.image" height="300" />
                </v-card> <v-card-actions>
                    <v-btn @click="likePost(post.id)" icon="mdi-heart" color="red-darken-3"></v-btn>
                    <v-btn color="black">
                        {{ post.likes }} Likes
                    </v-btn>
                </v-card-actions>
            </v-col>
        </v-row>
    </v-container>
</template>

<script>
import { ref, computed, onMounted } from "vue";

export default {
    setup() {
        const posts = ref([
            {
                id: 1,
                image:
                    "data:image/jpeg;base64,",
                caption: "User_1",
                likes: 10,
            },
            {
                id: 2,
                image:
                    "data:image/jpeg;base64,",
                caption: "user_2",
                likes: 1650,
            },
            {
                id: 3,
                image:
                    "data:image/jpeg;base64,",
                caption: "user_3",
                likes: 5,
            },
        ]);

        const likePost = (id) => {
            const index = posts.value.findIndex((post) => post.id === id);
            posts.value[index].likes++;
        };
        const openProfile = (id) => {
            this.$router.push({ name: "user", params: { id } });
        };

        return {
            posts,
            likePost,
            openProfile,
        };
    },
};
</script>
